mapper.select:
select id, username, tenant_id, status, gender, mobile_phone, email,
login_time, login_attempts, locked_time, password_update_time,
create_user, create_time, update_user, update_time
from tb_sys_user


        and username = #{username}


        and status = #{status}




        ORDER BY ${order} ${direction}


        ORDER BY id ASC

mapper.update:
UPDATE tb_sys_user
username = #{username}, password = #{password}, tenant_id = #{tenantId}, status = #{status}, gender = #{gender}, mobile_phone = #{mobilePhone}, email = #{email}, login_time = #{loginTime}, login_attempts = #{loginAttempts}, locked_time = #{lockedTime}, password_update_time = #{passwordUpdateTime}, create_user = #{createUser}, update_user = #{updateUser}, create_time = #{createTime}, update_time = #{updateTime}
WHERE id = #{id}
